import React, {useEffect,useState} from 'react'
import SearchBar from "./SearchBar"

function Database() {

const [search,setSearch] = useState("")
const [artworks, setArtworks] = useState([])

        useEffect(() => {
            fetch(`https://collectionapi.metmuseum.org/public/collection/v1/search?isHighlight=true&q=Highlight`)
            .then(r=> r.json())
            .then((data) => {
        // console.log(data.objectIDs)
                data.objectIDs.map((id) => {
                    fetch(`https://collectionapi.metmuseum.org/public/collection/v1/objects/${id}`)
                    .then(r=> r.json()) 
                    .then (
                        (id) => {
                        // console.log(id)
                       setArtworks(id)
                    })

                })
            })},[])

            function ArtworkContainer({artworks}) {

                const artworkCard = artworks.map((artwork)=> (
                    <ArtworkCard 
                      key={artwork.id}
                      artwork={artwork}
                    />
                ))

                return (
                  <ul className="cards">
                    {artworkCard} 
                  </ul>
                )
              }

              function ArtworkCard({artwork}) {
                const {medium, 
                        artist="Unknown Artist", 
                        title, 
                        objectDate, 
                        primaryImageSmall="https://upload.wikimedia.org/wikipedia/en/b/b9/MagrittePipe.jpg"} = artwork

                return (
                  <li className="card">
                    <img src={primaryImageSmall} alt={artist} />
                    <h4>{artist}</h4>
                    <p className="title">{title}</p>
                    <p>{objectDate}</p>
                    <p>{medium}</p>
                  </li>
                )
              }



return (
<div> 
    <SearchBar searchTerm={search} onSearchChange={setSearch}/>
    {ArtworkContainer}
    </div>
  )

}
export default Database
