import React, {useEffect,useState} from 'react'
// import SearchBar from "./SearchBar"
import NewContainer from "./NewContainer"
import SearchBar from "./SearchBar"


function Database({handleClick}) {

const [newWorks, setNewWorks] = useState([])
const [ids, setIds] = useState([])
const [search, setSearch] = useState([])

let arr =[]


useEffect(() => {
        fetch(`https://collectionapi.metmuseum.org/public/collection/v1/search?isHighlight=true&q=sun`)                                            
            .then(r=> r.json())
            .then((data) => {
                setIds([...data.objectIDs])
                // console.log(ids) 
              ids.map((id) => {
                fetch(`https://collectionapi.metmuseum.org/public/collection/v1/objects/${id}`)
                .then(r=> r.json())
                .then ((id) => {
                    arr.push({
                    title: id.title,
                    image: id.primaryImageSmall,
                    date: id.objectDate,
                    artist: id.artistDisplayName,
                    medium:id.medium,
                    reference:id.objectURL
                })
                    setNewWorks([...arr])
                })
            }
        )
    })} // eslint-disable-next-line react-hooks/exhaustive-deps
,[])


console.log(newWorks)

return (
    <div> 
    {/* <SearchBar searchTerm={search} onSearchChange={setSearch}/> */}
    <NewContainer newWorks={newWorks} handleClick={handleClick} />
    </div>
  )


}
export default Database
