import React, {useState}from 'react'

function NewCard({newWork, onAddNewWork}) {
  const   {medium, 
          artist, 
          title, 
          date, 
          image,
          reference}= newWork

function handleClick(){
  fetch("http://localhost:4000/artworks", {
    method: "POST",
    headers: {
      "Content-type": "application/json",
    },
  body: JSON.stringify({
    medium: medium,
    artist: artist,
    title: title,
    date: date,
    image: image,
  }),
})
.then(r => r.json())
.then((newWork) => onAddNewWork(newWork));
}
console.log(newWork)


  return (
    <li className="card">
      {image === "" ? 
