  const [medium, setMedium] = useState("");
  const [artist, setArtist] = useState("");
  const [title, setTitle] = useState("");
  const [date, setYear] = useState();
  const [image, setImage] = useState("");


function handleClick(){
  fetch("http://localhost:4000/artworks", {
    method: "POST",
    headers: {
      "Content-type": "application/json",
    },
  body: JSON.stringify({
        medium: medium,
        artist: artist,
        title: title,
        year: date,
        image: image,
  }),
})
.then(r => r.json())
}



  return (
    <li className="card">
      {newWork.image === "" ? 
      <a target="_blank" href="https://en.wikipedia.org/wiki/The_Treachery_of_Images" >
      <img src={"https://upload.wikimedia.org/wikipedia/en/b/b9/MagrittePipe.jpg"} alt={"MagrittePipe"}/>
      </a>
      : 
      <img src={newWork.image} alt={newWork.artist}/> }
      {artist === "" ? <h4>Unknown</h4> : <h4>{newWork.artist}</h4> }
      <a href={newWork.reference} target="_blank">
          <p className="title">{title}</p>
      </a>
      <p>{newWork.date}</p>
      <p>{newWork.medium}</p>
      <button className="button" onclick={handleClick}>âž•</button>
    </li>
  )
}



