import React, {useState, useEffect} from 'react'
import Header from "./Header"
import About from "./About"
import NavBar from "./NavBar"
import SearchBar from "./SearchBar"
import ArtworkContainer from "./ArtworkContainer"
import NewArtworkForm from "./NewArtworkForm"
import Database from "./Database"
import { Route, Switch } from "react-router-dom";

function App() {

  const [artworks, setArtworks] = useState([]) 
  const [search, setSearch] = useState("")
  const [newWorks, setNewWorks] = useState([])
  const [ids, setIds] = useState([])
  let arr =[]


  useEffect(() => {
    fetch ("http://localhost:4000/artworks")
    .then ((r) => r.json())
    .then (setArtworks)
  }, [])


  const displayArtworks = artworks.filter((artwork) => 
      Object.values(artwork).join("_").toLowerCase().includes(search.toLowerCase())
  )

  function addNewArtwork(newArtwork) {
    const updatedArtworkArray = [...artworks, newArtwork];
    setArtworks(updatedArtworkArray);
  }




    useEffect(() => {
            fetch(`https://collectionapi.metmuseum.org/public/collection/v1/search?isHighlight=true&q=highlight`)                                            
                .then(r=> r.json())
                .then((data) => {
                    setIds([...data.objectIDs])
                    // console.log(ids) 
                  ids.map((id) => {
                    fetch(`https://collectionapi.metmuseum.org/public/collection/v1/objects/${id}`)
                    .then(r=> r.json())
                    .then ((id) => {
                        arr.push({
                        title: id.title,
                        image: id.primaryImageSmall,
                        date: id.objectDate,
                        artist: id.artistDisplayName,
                        medium:id.medium,
                        reference:id.objectURL
                    })
                        setNewWorks([...arr])
                    })
                }
            )
        })} // eslint-disable-next-line react-hooks/exhaustive-deps
    ,[Database])

    console.log(newWorks)

  return (
    <div>

      <Header />
      <NavBar />
        <Switch>      
          <Route path="/about">
              <About />
          </Route>
          <Route  path="/database">
              <Database />
          </Route>
          <Route  path="/form">
              <NewArtworkForm onAddArtwork={addNewArtwork} />
          </Route>
          < Route exact path="/">
              <SearchBar searchTerm={search} onSearchChange={setSearch}/>
              <ArtworkContainer artworks={displayArtworks}/>
           </Route>
      </Switch>
    </div>
  )
}

export default App
